package JavaBasics;

public class Loopng {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=0;
		while (i<=20) {
			System.out.println(i);
			i++;
		}
		
		System.out.println("********");
		for (i=0;i<=20;i++) {
			System.out.println(i);
		}

	}

}
